import React from 'react';
import './button.css'
import { saveAs } from "file-saver";

const ButtonCV = () => {
    const saveFile = () => {
        saveAs(
            "/cv_2021_formateur.pdf",
            "example.pdf"
        );
    };
    return (
        <div className='gpt3__cv-container'>
            <a href="/cv_2021_formateur.pdf" download><button>Télécharger CV</button></a> 
            <button onClick={saveFile}>download</button>
        </div>
    );
};

export default ButtonCV;
